package com.axt.util;import java.text.SimpleDateFormat;import java.util.Calendar;import java.util.Date;import javax.servlet.http.HttpServletRequest;public class Common {	public static String nowDateAddDay4Format(Integer aDate, String pattern) {		SimpleDateFormat dateFormatf = new SimpleDateFormat(pattern);		Date date = new Date();		Calendar calendar = Calendar.getInstance();		calendar.setTime(date);		calendar.add(Calendar.DATE, aDate);		String d = dateFormatf.format(calendar.getTime());		return d;	}		public static  boolean isWindows(){		String osName = System.getProperty("os.name");		if(osName.toLowerCase().indexOf("windows") != -1){			return true;		}	    return false;	}}